import mongoose from "mongoose";
import { Sequelize, DataTypes } from "sequelize";
import _ from "lodash"
import casual from "casual";

// Mongo Connection
mongoose.Promise = global.Promise;
mongoose.connect("mongodb://localhost/widgets", {
    useNewUrlParser: true
})

const widgetSchema = new mongoose.Schema({
    name: {
        type: String
    },
    description: {
        type: String
    },
    price: {
        type: Number
    },
    soldout: {
        type: String
    },
    inventory: {
        type: String
    },
    stores: {
        type: Array
    }
})

const Widgets = mongoose.model('widgets', widgetSchema)

// Connect and creates a new sqlite database inisde my local memory -> all the rest is for sqlite
const sequalize = new Sequelize('sqlite::memory') 

const Categories = sequalize.define('categories', {
    category: DataTypes.STRING,
    description: DataTypes.STRING
});

Categories.sync({force: true}).then( ()=> {
    _.times(5, (i) => {
        Categories.create({
            category: casual.word,
            description: casual.sentence
        });
    });
});

export { Widgets, Categories };

